select B.*
from(
select a.*,rownum rowno
from(
select bo_no,
rep_no,rep_writer,rep_ip,rep_content, rep_date
from replyBook
where
BO_NO=#{searchVO.bo_no}
order by rep_no desc)A)B
where rowno between
#{startRow} and #{endRow}
