mapper.insert:
INSERT INTO pds (
pds_no,bo_no,pds_filename,
pds_savename,pds_mime,pds_size,
pds_fancysize
) VALUES (
pds_seq.nextval,
#{bo_no},#{pds_filename},#{pds_savename},
#{pds_mime},#{pds_size},#{pds_fancysize}
)

insert.if:
Insert into pds ( pds_no,bo_no,pds_filename, pds_savename,pds_mime,pds_size, pds_fancysize ) select pds_seq.nextval, a.* from(
select #{bo_no},#{pds.pds_filename},#{pds.pds_savename}, #{pds.pds_mime},#{pds.pds_size},#{pds.pds_fancysize} from dual
) a

mapper.delete:
delete from pds
where pds_no ins

#{pds_no}
